db_body.body:
CREATE OR REPLACE FUNCTION Dia_Semana(dia varchar) RETURNS varchar AS $$
        BEGIN
           if  dia = '1' then
               return 'Domingo';
           elsif  dia = '2' then
               return 'Segunda';
           elsif  dia = '3' then
               return 'Terça';
           elsif  dia = '4' then
               return 'Quarta';
           elsif  dia = '5' then
               return 'Quita';
           elsif  dia = '6' then
               return 'Sexta';
           elsif  dia = '7' then
               return 'Sábado';
           else 
               return 'Indefinido';
           end if;
        END;
$$ LANGUAGE plpgsql;
