db_script.script:
CREATE OR REPLACE FUNCTION xgp_monta_animal_log_function()
RETURNS trigger AS 
$body$
BEGIN
    IF (TG_OP = 'INSERT') THEN
        INSERT INTO lg_xgp_monta_animal 
        (id_monta, id_monta_animal, dml, data_dml) VALUES 
        (new.id_monta, new.id_monta_animal, 'I', current_timestamp);
	    RETURN NEW;
    ELSIF (TG_OP = 'UPDATE') THEN
        INSERT INTO lg_xgp_monta_animal 
        (id_monta, id_monta_animal, dml, data_dml) VALUES 
        (new.id_monta, new.id_monta_animal, 'U', current_timestamp);
	    RETURN NEW;
    ELSIF (TG_OP = 'DELETE') THEN
        INSERT INTO lg_xgp_monta_animal 
        (id_monta, id_monta_animal, dml, data_dml) VALUES 
        (old.id_monta, old.id_monta_animal, 'D', current_timestamp);
        RETURN OLD;
    END IF;
RETURN NULL;
END;
$body$
LANGUAGE plpgsql;
