db_script.script:
CREATE OR REPLACE FUNCTION xgp_aparte_log_function()
RETURNS trigger AS 
$body$
BEGIN
    IF (TG_OP = 'INSERT') THEN
        INSERT INTO lg_xgp_aparte
        (id_aparte, dml, data_dml) VALUES 
        (new.id_aparte, 'I', current_timestamp);
	    RETURN NEW;
    ELSIF (TG_OP = 'UPDATE') THEN
        INSERT INTO lg_xgp_aparte
        (id_aparte, dml, data_dml) VALUES 
        (new.id_aparte, 'U', current_timestamp);
	    RETURN NEW;
    ELSIF (TG_OP = 'DELETE') THEN
        INSERT INTO lg_xgp_aparte
        (id_aparte, dml, data_dml) VALUES 
        (old.id_aparte, 'D', current_timestamp);
        RETURN OLD;
    END IF;
RETURN NULL;
END;
$body$
LANGUAGE plpgsql;
